// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.6;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "./SharedStructs.sol";
import "./ILilypadController.sol";
import "./ILilypadStorage.sol";
import "./ILilypadPayments.sol";
import "./ILilypadMediation.sol";

contract LilypadController is ILilypadController, Ownable, Initializable {

  /**
   * Types
   */

  address private storageAddress;
  address private userAddress;
  address private paymentsAddress;
  address private mediationAddress;
  address private jobCreatorAddress;

  ILilypadStorage private storageContract;
  ILilypadPayments private paymentsContract;
  ILilypadMediationHandler private mediationContract;

  /**
   * Init
   */

  // https://docs.openzeppelin.com/upgrades-plugins/1.x/writing-upgradeable
  function initialize(
    address _storageAddress,
    address _usersAddress,
    address _paymentsAddress,
    address _mediationAddress,
    address _jobCreatorAddress
  ) public initializer {
    setStorageAddress(_storageAddress);
    setUsersAddress(_usersAddress);
    setPaymentsAddress(_paymentsAddress);
    setMediationAddress(_mediationAddress);
    setJobCreatorAddress(_jobCreatorAddress);
  }

  function setStorageAddress(address _storageAddress) public onlyOwner {
    require(_storageAddress != address(0), "Storage address");
    storageAddress = _storageAddress;
    storageContract = ILilypadStorage(storageAddress);
  }

  function getStorageAddress() public view returns(address) {
    return storageAddress;
  }

  function setUsersAddress(address _usersAddress) public onlyOwner {
    require(_usersAddress != address(0), "Users address");
    userAddress = _usersAddress;
  }

  function getUsersAddress() public view returns(address) {
    return userAddress;
  }

  function setPaymentsAddress(address _paymentsAddress) public onlyOwner {
    require(_paymentsAddress != address(0), "Payments address");
    paymentsAddress = _paymentsAddress;
    paymentsContract = ILilypadPayments(_paymentsAddress);
  }

  function getPaymentsAddress() public view returns(address) {
    return paymentsAddress;
  }

  function setMediationAddress(address _mediationAddress) public onlyOwner {
    require(_mediationAddress != address(0), "Mediation address");
    mediationAddress = _mediationAddress;
    mediationContract = ILilypadMediationHandler(_mediationAddress);
  }

  function getMediationAddress() public view returns(address) {
    return mediationAddress;
  }

  function setJobCreatorAddress(address _jobCreatorAddress) public onlyOwner {
    require(_jobCreatorAddress != address(0), "JobCreator address");
    jobCreatorAddress = _jobCreatorAddress;
  }

  function getJobCreatorAddress() public view returns(address) {
    return jobCreatorAddress;
  }

  /**
   * Agreements
   */

  // * create the deal if not exists
  // * otherwise compare the values to ensure 2 sided agreement
  // * check the RP or JC is calling this
  // * if RP:
  //    * mark the deal as RP agreed
  //    * pay in the timeout collateral
  // * if JC:
  //    * mark the deal as JC agreed
  //    * pay in the payment collateral and timeout collateral
  // * if both sides have agreed then mark the deal as agreed
  // * emit the event
  function agree(
    string memory dealId,
    SharedStructs.DealMembers memory members,
    SharedStructs.DealTimeouts memory timeouts,
    SharedStructs.DealPricing memory pricing
  ) public override returns (SharedStructs.Agreement memory) {
    SharedStructs.Deal memory deal = storageContract.ensureDeal(
      dealId,
      members,
      timeouts,
      pricing
    );
    bool isResourceProvider = tx.origin == deal.members.resourceProvider;
    bool isJobCreator = tx.origin == deal.members.jobCreator;
    require(isResourceProvider || isJobCreator, "Only RP / JC");

    if(isResourceProvider) {
      storageContract.agreeResourceProvider(dealId);
      paymentsContract.agreeResourceProvider(
        dealId,
        deal.members.resourceProvider,
        deal.timeouts.submitResults.collateral
      );
    }
    else if(isJobCreator) {
      storageContract.agreeJobCreator(dealId);
      paymentsContract.agreeJobCreator(
        dealId,
        deal.members.jobCreator,
        deal.pricing.paymentCollateral,
        // the JC paus the judge results collateral
        deal.timeouts.judgeResults.collateral
      );
    }
    return storageContract.getAgreement(dealId);
  }

  /**
   * Results
   */

  // * check the RP is calling this
  // * mark the deal as results submitted
  // * calculate the cost of the job
  // * calculate the job collateral based on the multiple
  // * work out the difference between the timeout and results collateral
  // * pay the difference into / out of the contract to the RP
  // * emit the event
  function addResult(
    string memory dealId,
    string memory resultsId,
    // this is the CID of the actual data
    // otherwise onchain clients cannot know the actual data they want to fetch
    string memory dataId,
    uint256 instructionCount
  ) public override {
    require(storageContract.isState(dealId, SharedStructs.AgreementState.DealAgreed), "DealAgreed");
    SharedStructs.Deal memory deal = storageContract.getDeal(dealId);
    require(deal.members.resourceProvider == tx.origin, "Only RP");

    storageContract.addResult(
      dealId,
      resultsId,
      dataId,
      instructionCount
    );

    // how many multiple of the job cost must the RP put up as collateral
    // we need to do this after having added the result otherwise
    // we don't know the instruction count
    uint256 resultsCollateral = storageContract.getResultsCollateral(dealId);

    paymentsContract.addResult(
      dealId,
      deal.members.resourceProvider,
      resultsCollateral,
      // this is the RP adding a results so they get their submit results timeout collateral back
      deal.timeouts.submitResults.collateral
    );
  }

  // * check the JC is calling this
  // * check we are in Submitted state
  // * mark the deal as results accepted
  // * calculate the cost of the job
  // * deduct the cost of the job from the JC payment collateral
  // * pay the RP the cost of the job
  // * refund the RP the results collateral
  // * refund the JC the job collateral minus the cost
  // * refund the JC the timeout collateral
  function acceptResult(
    string memory dealId
  ) public override {
    require(storageContract.isState(dealId, SharedStructs.AgreementState.ResultsSubmitted), "ResultsSubmitted");
    SharedStructs.Deal memory deal = storageContract.getDeal(dealId);
    require(deal.members.jobCreator == tx.origin, "Only JC");

    uint256 jobCost = storageContract.getJobCost(dealId);
    uint256 resultsCollateral = storageContract.getResultsCollateral(dealId);

    storageContract.acceptResult(dealId);
    paymentsContract.acceptResult(
      dealId,
      deal.members.resourceProvider,
      deal.members.jobCreator,
      jobCost,
      deal.pricing.paymentCollateral,
      resultsCollateral,
      // this is the JC judging their result so they get their timeout collateral back
      deal.timeouts.judgeResults.collateral
    );
  }

  // * check the JC is calling this
  // * check we are in Submitted state
  // * check the mediator is in the list of RP trusted mediators
  // * mark the deal as results checked
  // * charge the JC the mediation fee
  // * refund the JC the timeout collateral
  // * emit the Mediation event so the mediator kicks in
  function checkResult(
    string memory dealId
  ) public override {
    require(storageContract.isState(dealId, SharedStructs.AgreementState.ResultsSubmitted), "ResultsSubmitted");
    SharedStructs.Deal memory deal = storageContract.getDeal(dealId);
    require(deal.members.jobCreator == tx.origin, "Only JC");

    // this function will require that the mediator is in the RP's list of trusted mediators
    storageContract.checkResult(dealId);
    paymentsContract.checkResult(
      dealId,
      deal.members.jobCreator,
      // this is the JC judging their result so they get their timeout collateral back
      deal.timeouts.judgeResults.collateral,
      deal.pricing.mediationFee
    );

    // trigger the mediation process by calling the contract
    mediationContract.mediationRequest(deal);
  }

  /**
   * Mediation
   */

  // the mediator calls this to say that the resource provider did the correct job
  // * check the state is ResultsChecked
  // * check the mediator is calling this
  // * mark the deal as mediation accepted
  // * refund the JC what is left from the payment collateral (if any)
  // * pay the RP the cost of the job
  // * refund the RP the results collateral
  // * pay the mediator for mediating
  function mediationAcceptResult(
    string memory dealId
  ) public override {
    require(mediationAddress == _msgSender(), "Only mediation");
    require(_canMediateResult(dealId), "Cannot mediate");

    SharedStructs.Deal memory deal = storageContract.getDeal(dealId);
    uint256 jobCost = storageContract.getJobCost(dealId);
    uint256 resultsCollateral = storageContract.getResultsCollateral(dealId);

    storageContract.mediationAcceptResult(dealId);
    paymentsContract.mediationAcceptResult(
      dealId,
      deal.members.resourceProvider,
      deal.members.jobCreator,
      jobCost,
      deal.pricing.paymentCollateral,
      resultsCollateral,
      deal.pricing.mediationFee
    );
  }

  // the mediator calls this to say that the resource provider did the bad job
  // * check the state is ResultsChecked
  // * check the mediator is calling this
  // * mark the deal as mediation rejected
  // * refund the JC their payment collateral
  // * slash the RP's results collateral
  // * pay the mediator for mediating
  function mediationRejectResult(
    string memory dealId
  ) public override {
    // only the current mediation contract can call this
    require(mediationAddress == _msgSender(), "Only mediation");
    require(_canMediateResult(dealId), "Cannot mediate");

    SharedStructs.Deal memory deal = storageContract.getDeal(dealId);
    uint256 resultsCollateral = storageContract.getResultsCollateral(dealId);

    storageContract.mediationRejectResult(dealId);
    paymentsContract.mediationRejectResult(
      dealId,
      deal.members.resourceProvider,
      deal.members.jobCreator,
      deal.pricing.paymentCollateral,
      resultsCollateral,
      deal.pricing.mediationFee
    );
  }

  function _canMediateResult(
    string memory dealId 
  ) private returns (bool) {
    require(storageContract.isState(dealId, SharedStructs.AgreementState.ResultsChecked), "ResultsChecked");
    return true;
  }

  /**
   * Timeouts
   */

  function timeoutAgree(
    string memory dealId
  ) public override {
    SharedStructs.Deal memory deal = storageContract.getDeal(dealId);
    SharedStructs.Agreement memory agreement = storageContract.getAgreement(dealId);
    require(deal.members.jobCreator == tx.origin || deal.members.resourceProvider == tx.origin, "Only JC or RP");
    require(agreement.state == SharedStructs.AgreementState.DealNegotiating, "Not correct state");
    require(block.timestamp > agreement.dealCreatedAt + deal.timeouts.agree.timeout, "Not timed out");
    storageContract.timeoutAgree(dealId);
    if (agreement.resourceProviderAgreedAt > 0) {
      // this is an RP refund
      paymentsContract.timeoutAgreeRefundResourceProvider(
        dealId,
        deal.members.resourceProvider,
        deal.timeouts.submitResults.collateral
      );
    } else if (agreement.jobCreatorAgreedAt > 0) {
      // this is an JC refund
      paymentsContract.timeoutAgreeRefundJobCreator(
        dealId,
        deal.members.jobCreator,
        deal.pricing.paymentCollateral,
        deal.timeouts.submitResults.collateral
      );
    }
  }

  // the job creator calls this after the timeout has passed and there are no results submitted
  // * check the JC is calling this
  // * mark the deal as timedout
  // * pay back the JC's job collateral
  // * slash the RP's results collateral
  // * emit the event
  function timeoutSubmitResult(
    string memory dealId
  ) public override {
    SharedStructs.Deal memory deal = storageContract.getDeal(dealId);
    SharedStructs.Agreement memory agreement = storageContract.getAgreement(dealId);
    require(deal.members.jobCreator == tx.origin, "Only JC");
    require(agreement.state == SharedStructs.AgreementState.DealAgreed, "Not correct state");
    require(block.timestamp > agreement.dealAgreedAt + deal.timeouts.submitResults.timeout, "Not timed out");
    storageContract.timeoutSubmitResult(dealId);
    paymentsContract.timeoutSubmitResult(
      dealId,
      deal.members.resourceProvider,
      deal.members.jobCreator,
      deal.pricing.paymentCollateral,
      deal.timeouts.submitResults.collateral
    );
  }

  // the resource provider calls this after the timeout has passed after submitting results
  // and the job creator has not yet submitted their judgement on those results
  // * check the RP is calling this
  // * mark the deal as timedout
  // * pay back the RP's results collateral
  // * pay the RP the cost of the job
  // * slash the JC's timeout collateral
  // * slash the JC's job collateral
  // * emit the event
  function timeoutJudgeResult(
    string memory dealId
  ) public override {
    SharedStructs.Deal memory deal = storageContract.getDeal(dealId);
    SharedStructs.Agreement memory agreement = storageContract.getAgreement(dealId);
    require(deal.members.resourceProvider == tx.origin, "Only RP");
    require(agreement.state == SharedStructs.AgreementState.ResultsSubmitted, "Not correct state");
    require(block.timestamp > agreement.resultsSubmittedAt + deal.timeouts.judgeResults.timeout, "Not timed out");
    uint256 resultsCollateral = storageContract.getResultsCollateral(dealId);
    storageContract.timeoutJudgeResult(dealId);    
    paymentsContract.timeoutJudgeResult(
      dealId,
      deal.members.resourceProvider,
      deal.members.jobCreator,
      resultsCollateral,
      deal.timeouts.judgeResults.collateral
    );
  }

  // either the JC or RP call this after the timeout has passed after results being checked
  // this refunds both the payment and results collateral to both the JC and RP
  // * check the RP or JC is calling this
  // * mark the deal as timedout
  // * pay back the RP's results collateral
  // * pay back the JC's paymnet collateral
  // * emit the event
  function timeoutMediateResult(
    string memory dealId
  ) public override {
    SharedStructs.Deal memory deal = storageContract.getDeal(dealId);
    SharedStructs.Agreement memory agreement = storageContract.getAgreement(dealId);
    require(deal.members.resourceProvider == tx.origin || deal.members.jobCreator == tx.origin, "Only RP or JC");
    require(agreement.state == SharedStructs.AgreementState.ResultsChecked, "Not correct state");
    require(block.timestamp > agreement.resultsSubmittedAt + deal.timeouts.judgeResults.timeout, "Not timed out");
    uint256 resultsCollateral = storageContract.getResultsCollateral(dealId);
    storageContract.timeoutMediateResult(dealId);
    paymentsContract.timeoutMediateResult(
      dealId,
      deal.members.resourceProvider,
      deal.members.jobCreator,
      deal.pricing.paymentCollateral,
      resultsCollateral,
      deal.pricing.mediationFee
    );
  }
}

contracts/LilypadMediationRandom.sol.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.6;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "./ControllerOwnable.sol";
import "./SharedStructs.sol";
import "./ILilypadMediation.sol";

contract LilypadMediationRandom is ControllerOwnable, Initializable {

  // keep track of which mediators were choosen for which deals
  mapping(string => address) private mediators;

  event MediationRequested(
    string dealId,
    address mediator
  );

  /**
   * Init
   */

  // https://docs.openzeppelin.com/upgrades-plugins/1.x/writing-upgradeable
  function initialize() public initializer {

  }

  // this is called by the controller contract
  function mediationRequest(
    SharedStructs.Deal memory deal
  ) public onlyController {
    uint randomIndex = uint(keccak256(abi.encodePacked(block.timestamp, deal.dealId))) % deal.members.mediators.length;
    address mediator = deal.members.mediators[randomIndex];
    require(mediator != address(0), "mediator cannot be 0x0");
    mediators[deal.dealId] = mediator;
    emit MediationRequested(deal.dealId, mediator);
  }

  function getMediator(
    string memory dealId
  ) public view returns(address) {
    return mediators[dealId];
  }

  // call the controller contract as a ILilypadMediationRequester
  function mediationAcceptResult(
    string memory dealId
  ) public {
    // check the tx.origin is the same mediator that was picked
    require(mediators[dealId] != address(0), "mediator cannot be 0x0");
    require(mediators[dealId] == tx.origin, "tx.origin must be the mediator");
    // call the controller contract
    ILilypadMediationRequester(getControllerAddress()).mediationAcceptResult(dealId);
  }

  // call the controller contract as a ILilypadMediationRequester
  function mediationRejectResult(
    string memory dealId
  ) public {
    // check the tx.origin is the same mediator that was picked
    require(mediators[dealId] != address(0), "mediator cannot be 0x0");
    require(mediators[dealId] == tx.origin, "tx.origin must be the mediator");
    // call the controller contract
    ILilypadMediationRequester(getControllerAddress()).mediationRejectResult(dealId);
  }
}

contracts/LilypadOnChainJobCreator.sol.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.6;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "./SharedStructs.sol";
import "./ILilypadJobManager.sol";
import "./ILilypadJobClient.sol";
import "./ControllerOwnable.sol";
import "./ILilypadToken.sol";

contract LilypadOnChainJobCreator is ILilypadJobManager, ControllerOwnable, Initializable {

  // the token contract
  // we check to see what allowance has been granted to be spent on behalf
  // of the customer of a job
  address private tokenAddress;
  ILilypadToken private tokenContract;

  // the minimum amount that must be "approved" on the smart contract for the solver to spend
  // for it to consider running a job for a client
  // the solver will update this as the market pricing changes
  uint256 public requiredDeposit;

  // auto increment job id
  uint256 public nextJobID;

  // map of job id onto offer
  mapping(uint256 => SharedStructs.JobOffer) private jobOffers;

  event JobAdded(
    uint256 id,
    address calling_contract,
    address payee,
    string module,
    string[] inputs
  );

  /**
   * Init
   */

  // https://docs.openzeppelin.com/upgrades-plugins/1.x/writing-upgradeable
  function initialize(
    address _tokenAddress
  ) public initializer {
    setTokenAddress(_tokenAddress);
    nextJobID = 0;
  }

  function setTokenAddress(address _tokenAddress) public onlyOwner {
    require(_tokenAddress != address(0), "Token address");
    tokenAddress = _tokenAddress;
    tokenContract = ILilypadToken(tokenAddress);
  }

  function getTokenAddress() public view returns (address) {
    return tokenAddress;
  }

  function setRequiredDeposit(uint256 cost) public onlyController {
    require(cost > 0, "Min deposit");
    requiredDeposit = cost;
  }

  function getRequiredDeposit() public view returns (uint256) {
    return requiredDeposit;
  }

  // called by on-chain clients to make an offer for a job
  // this will return a ticketID which is a unique onchain identifier for the job
  function runJob(
    // what is the module name we are making an offer for
    string memory module,
    // an array of key=value pairs that will be the inputs to the job
    string[] memory inputs,
    // the address of the client who is paying for the job
    // they must have called the increaseAllowance function
    // giving the controller (i.e. solver) permission to spend their tokens
    address payee
  ) public override returns (uint256) {
    // this makes sure that the person paying for the job has
    // already called "approve" on the token contract so the solver can
    // work on it's behalf
    require(tokenContract.allowance(payee, getControllerAddress()) >= requiredDeposit, "Token allowance not enough");

    nextJobID = nextJobID + 1;
    jobOffers[nextJobID] = SharedStructs.JobOffer(
      nextJobID,
      msg.sender,
      payee,
      module,
      inputs
    );
    emit JobAdded(
      nextJobID,
      msg.sender,
      payee,
      module,
      inputs
    );

    return nextJobID;
  }

  // this is called by the solver once we've got results out of the controller
  // it will call the "resultsAdded" function on the original client contract
  function submitResults(
    uint256 id,
    string memory dealId,
    string memory dataId
  ) public onlyController override {
    SharedStructs.JobOffer storage offer = jobOffers[id];
    require(offer.id != 0, "Job not found");
    ILilypadJobClient(offer.calling_contract).submitResults(
      id,
      dealId,
      dataId
    );
  }
}

contracts/LilypadPayments.sol.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.6;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "./ILilypadToken.sol";
import "./ControllerOwnable.sol";
import "./ILilypadPayments.sol";

// import "@openzeppelin/contracts/utils/Strings.sol";
// import "hardhat/console.sol";
// console.log("ensureDeal");
// console.log(Strings.toString(uint256(SharedStructs.AgreementState.DealNegotiating)));
// console.log(Strings.toString(uint256(agreements[dealId].state)));

contract LilypadPayments is ControllerOwnable, Initializable {

  /**
   * Types
   */

  // the address of the LilypadToken contract
  address private tokenAddress;
  ILilypadToken private tokenContract;

  // used to cut off upgrades for the remote contract
  bool private canChangeTokenAddress = true;

  /**
   * Enums
   */
  enum PaymentReason {

    // the money the JC puts up to pay for the job
    PaymentCollateral,

    // the money the RP puts up to attest it's results are correct
    ResultsCollateral,

    // the money the RP, JC and Mediator all put up to prevent timeouts
    TimeoutCollateral,

    // the money the RP gets paid for the job for running it successfully
    JobPayment,

    // the money the JC pays the mediator for resolving a dispute
    MediationFee
  }

  enum PaymentDirection {

    // money flowing into the contract
    // i.e. we GET paid
    PaidIn,

    // money paid out to services
    // i.e. we are PAYING
    PaidOut,

    // collateral that is locked up being refunded
    Refunded,

    // collateral that is locked up being slashed
    Slashed
  }

  /**
   * Events
   */
  event Payment(
    string dealId,
    address payee,
    uint256 amount,
    PaymentReason reason,
    PaymentDirection direction
  );

  /**
   * Init
   */

  // used for debugging
  mapping(address => string) private accountNames;

  // https://docs.openzeppelin.com/upgrades-plugins/1.x/writing-upgradeable
  function initialize(
    address _tokenAddress
  ) public initializer {
    setTokenAddress(_tokenAddress);

    // this is only for debugging
    // accountNames[address(0xf39Fd6e51aad88F6F4ce6aB8827279cffFb92266)] = "admin";
    // accountNames[address(0x70997970C51812dc3A010C7d01b50e0d17dc79C8)] = "faucet";
    // accountNames[address(0x3C44CdDdB6a900fa2b585dd299e03d12FA4293BC)] = "solver";
    // accountNames[address(0x90F79bf6EB2c4f870365E785982E1f101E93b906)] = "mediator";
    // accountNames[address(0x15d34AAf54267DB7D7c367839AAf71A00a2C6A65)] = "resource_provider";
    // accountNames[address(0x9965507D1a55bcC2695C58ba16FB37d819B0A4dc)] = "job_creator";
    // accountNames[address(0x976EA74026E726554dB657fA54763abd0C3a0aa9)] = "directory";
  }

  // the LilypadToken we are calling payinEscrow and payoutEscrow on
  function setTokenAddress(address _tokenAddress) public onlyOwner {
    require(_tokenAddress != address(0), "LilepadPayments: Token address must be defined");
    require(canChangeTokenAddress, "LilypadToken: canChangeTokenAddress is disabled");
    tokenAddress = _tokenAddress;
    tokenContract = ILilypadToken(_tokenAddress);
  }

  function getTokenAddress() public view returns(address) {
    return tokenAddress;
  }

  // set for canChangePaymentsAddress
  function disableChangeTokenAddress() public onlyOwner {
    canChangeTokenAddress = false;
  }

  /**
   * Controller handlers
   * 
   * these methods are called by the controller to wrap various payment
   * scenarios - hence they are all onlyOwner
   */

  /**
   * Agreements
   */

  // * pay in the timeout collateral
  function agreeResourceProvider(
    string memory dealId,
    address resourceProvider,
    uint256 timeoutCollateral
  ) public onlyController {
    // we check this here to double check who we are about to charge (the RP)
    // is who signed the TX and so we can take the money
    require(tx.origin == resourceProvider, "LilypadPayments: Can only be called by the RP");
    _payEscrow(
      dealId,
      timeoutCollateral,
      PaymentReason.TimeoutCollateral
    );
  }

  // * pay in the payment collateral and timeout collateral
  function agreeJobCreator(
    string memory dealId,
    address jobCreator,
    uint256 paymentCollateral,
    uint256 timeoutCollateral
  ) public onlyController {
    require(tx.origin == jobCreator, "LilypadPayments: Can only be called by the JC");
    _payEscrow(
      dealId,
      paymentCollateral,
      PaymentReason.PaymentCollateral
    );
    _payEscrow(
      dealId,
      timeoutCollateral,
      PaymentReason.TimeoutCollateral
    );
  }

  /**
   * Results
   */

  // * pay back the timeout collateral
  // * pay in the results collateral
  function addResult(
    string memory dealId,
    address resourceProvider,
    uint256 resultsCollateral,
    uint256 timeoutCollateral
  ) public onlyController {
    require(tx.origin == resourceProvider, "LilypadPayments: Can only be called by the RP");
    _payEscrow(
      dealId,
      resultsCollateral,
      PaymentReason.ResultsCollateral
    );
    _refundEscrow(
      dealId,
      resourceProvider,
      timeoutCollateral,
      PaymentReason.TimeoutCollateral
    );
  }

  // * pay the RP the job cost
  // * refund the RP the results collateral
  // * refund the JC the job collateral minus the job cost
  // * refund the JC the timeout collateral
  function acceptResult(
    string memory dealId,
    address resourceProvider,
    address jobCreator,
    uint256 jobCost,
    uint256 paymentCollateral,
    uint256 resultsCollateral,
    uint256 timeoutCollateral
  ) public onlyController {
    require(tx.origin == jobCreator, "LilypadPayments: Can only be called by the JC");

    // what if the final job cost is more than the payment collateral?
    // well - we have to cap the job cost at that collateral
    // true - the RP has lost money but they agreed to the deal
    uint256 actualPayment = jobCost;
    uint256 jcRefund = 0;
    if(jobCost > paymentCollateral) {
      actualPayment = paymentCollateral;
    } else {
      jcRefund = paymentCollateral - jobCost;
    }

    // pay the RP the actualPayment
    _payOut(
      dealId,
      jobCreator,
      resourceProvider,
      actualPayment,
      PaymentReason.JobPayment
    );

    // if the job cost more than the payment collateral then we shold not go negative
    // otherwise we are paying out more than the JC has put in
    //
    // the RP is loosing out a bit here but they agreed to doing the job
    if(jcRefund > 0) {
      _refundEscrow(
        dealId,
        jobCreator,
        jcRefund,
        PaymentReason.PaymentCollateral
      );
    }

    // refund the JC timeout collateral
    _refundEscrow(
      dealId,
      jobCreator,
      timeoutCollateral,
      PaymentReason.TimeoutCollateral
    );

    // refund the RP results collateral
    _refundEscrow(
      dealId,
      resourceProvider,
      resultsCollateral,
      PaymentReason.ResultsCollateral
    );
  }

  // * charge the JC the mediation fee
  // * refund the JC the timeout collateral
  function checkResult(
    string memory dealId,
    address jobCreator,
    uint256 timeoutCollateral,
    uint256 mediationFee
  ) public onlyController {
    require(tx.origin == jobCreator, "LilypadPayments: Can only be called by the JC");

    // the refund of the timeout collateral
    _refundEscrow(
      dealId,
      jobCreator,
      timeoutCollateral,
      PaymentReason.TimeoutCollateral
    );

    // the payment of the mediation fee
    _payEscrow(
      dealId,
      mediationFee,
      PaymentReason.MediationFee
    );
  }

  /**
   * Mediation
   */

  // * pay the RP the job cost
  // * refund the RP the results collateral
  // * refund the JC the job collateral minus the job cost
  // * pay the mediator for mediating
  function mediationAcceptResult(
    string memory dealId,
    address resourceProvider,
    address jobCreator,
    uint256 jobCost,
    uint256 paymentCollateral,
    uint256 resultsCollateral,
    uint256 mediationFee
  ) public onlyController {
    uint256 actualPayment = jobCost;
    uint256 jcRefund = 0;
    if(jobCost > paymentCollateral) {
      actualPayment = paymentCollateral;
    } else {
      jcRefund = paymentCollateral - jobCost;
    }

    // pay the RP the job cost from the JC
    _payOut(
      dealId,
      jobCreator,
      resourceProvider,
      actualPayment,
      PaymentReason.JobPayment
    );

    // pay the mediator the fee from the JC
    _payOut(
      dealId,
      jobCreator,
      tx.origin,
      mediationFee,
      PaymentReason.MediationFee
    );

    // if the job cost more than the payment collateral then we shold not go negative
    // otherwise we are paying out more than the JC has put in
    //
    // the RP is loosing out a bit here but they agreed to doing the job
    if(jcRefund > 0) {

      // refund the JC the diff between payment collateral and job cost
      _refundEscrow(
        dealId,
        jobCreator,
        jcRefund,
        PaymentReason.PaymentCollateral
      );
    }

    // refund the RP the results collateral
    _refundEscrow(
      dealId,
      resourceProvider,
      resultsCollateral,
      PaymentReason.ResultsCollateral
    );
  }

  // * refund the JC their payment collateral
  // * slash the RP's results collateral
  // * pay the mediator for mediating
  function mediationRejectResult(
    string memory dealId,
    address resourceProvider,
    address jobCreator,
    uint256 paymentCollateral,
    uint256 resultsCollateral,
    uint256 mediationFee
  ) public onlyController {
    // refund the JC their payment collateral
    _refundEscrow(
      dealId,
      jobCreator,
      paymentCollateral,
      PaymentReason.PaymentCollateral
    );

    // pay the mediator the fee from the JC
    _payOut(
      dealId,
      jobCreator,
      tx.origin,
      mediationFee,
      PaymentReason.MediationFee
    );

    // slash the RP
    _slashEscrow(
      dealId,
      resourceProvider,
      resultsCollateral,
      PaymentReason.ResultsCollateral
    );
  }

  /**
   * Timeouts
   */
  function timeoutAgreeRefundResourceProvider(
    string memory dealId,
    address resourceProvider,
    uint256 timeoutCollateral
  ) public onlyController {
    require(tx.origin == resourceProvider, "LilypadPayments: Can only be called by the RP");
    // the refund of the job collateral to the JC
    _refundEscrow(
      dealId,
      resourceProvider,
      timeoutCollateral,
      PaymentReason.TimeoutCollateral
    );
  }

  function timeoutAgreeRefundJobCreator(
    string memory dealId,
    address jobCreator,
    uint256 paymentCollateral,
    uint256 timeoutCollateral
  ) public onlyController {
    require(tx.origin == jobCreator, "LilypadPayments: Can only be called by the JC");
    // the refund of the job collateral to the JC
    _refundEscrow(
      dealId,
      jobCreator,
      paymentCollateral,
      PaymentReason.PaymentCollateral
    );

    // the refund of the job collateral to the JC
    _refundEscrow(
      dealId,
      jobCreator,
      timeoutCollateral,
      PaymentReason.TimeoutCollateral
    );
  }

  // * pay back the JC's job collateral
  // * pay back the JC's timeout collateral
  // * slash the RP's results collateral
  function timeoutSubmitResults(
    string memory dealId,
    address resourceProvider,
    address jobCreator,
    uint256 paymentCollateral,
    uint256 timeoutCollateral
  ) public onlyController {
    require(tx.origin == jobCreator, "LilypadPayments: Can only be called by the JC");
    // the refund of the job collateral to the JC
    _refundEscrow(
      dealId,
      jobCreator,
      paymentCollateral,
      PaymentReason.PaymentCollateral
    );

    // the refund of the job collateral to the JC
    _refundEscrow(
      dealId,
      jobCreator,
      timeoutCollateral,
      PaymentReason.TimeoutCollateral
    );

    // the slashing of the timeout collateral for the RP
    _slashEscrow(
      dealId,
      resourceProvider,
      timeoutCollateral,
      PaymentReason.TimeoutCollateral
    );
  }

  // * pay back the RP's results collateral
  // * pay the RP the cost of the job
  // * slash the JC's timeout collateral
  // * slash the JC's job collateral
  function timeoutJudgeResults(
    string memory dealId,
    address resourceProvider,
    address jobCreator,
    uint256 resultsCollateral,
    uint256 timeoutCollateral
  ) public onlyController {
    require(tx.origin == resourceProvider, "LilypadPayments: Can only be called by the RP");
    // the refund of the results collateral to the RP
    _refundEscrow(
      dealId,
      resourceProvider,
      resultsCollateral,
      PaymentReason.ResultsCollateral
    );

    // the slashing of the timeout collateral for the RP
    _slashEscrow(
      dealId,
      jobCreator,
      timeoutCollateral,
      PaymentReason.TimeoutCollateral
    );
  }

  // * pay back the RP's results collateral
  // * pay back the JC's payment collateral
  // * pay back the JC's mediation fee
  function timeoutMediateResult(
    string memory dealId,
    address resourceProvider,
    address jobCreator,
    uint256 paymentCollateral,
    uint256 resultsCollateral,
    uint256 mediationFee
  ) public onlyController {
    require(tx.origin == resourceProvider || tx.origin == jobCreator, "LilypadPayments: Can only be called by the RP or JC");
    // the refund of the results collateral to the RP
    _refundEscrow(
      dealId,
      resourceProvider,
      resultsCollateral,
      PaymentReason.ResultsCollateral
    );

    // the refund of the payment collateral to the JC
    _refundEscrow(
      dealId,
      jobCreator,
      paymentCollateral,
      PaymentReason.PaymentCollateral
    );

    // the refund of the mediation fee to the JC
    _refundEscrow(
      dealId,
      jobCreator,
      mediationFee,
      PaymentReason.MediationFee
    );
  }

  /**
   * Payment utils
   */


  function _payEscrow(
    string memory dealId,
    uint256 amount,
    PaymentReason reason
  ) private {
    // we check they have that much in their token balance before moving to tokens to us
    require(tokenContract.balanceOf(tx.origin) >= amount, "LilypadPayments: Insufficient balance");

    // console.log("_payEscrow");
    // console.log(accountNames[tx.origin]);
    // console.log(amount);

    bool success = tokenContract.payEscrow(amount);
    require(success, "LilypadPayments: Pay escrow failed");

    emit Payment(
      dealId,
      tx.origin,
      amount,
      reason,
      PaymentDirection.PaidIn
    );
  }

  function _refundEscrow(
    string memory dealId,
    address toAddress,
    uint256 amount,
    PaymentReason reason
  ) private {
    // console.log("_refundEscrow");
    // console.log(accountNames[toAddress]);
    // console.log(amount);

    bool success = tokenContract.refundEscrow(toAddress, amount);
    require(success, "LilypadPayments: Refund escrow failed");

    emit Payment(
      dealId,
      toAddress,
      amount,
      reason,
      PaymentDirection.Refunded
    );
  }

  function _payOut(
    string memory dealId,
    address fromAddress,
    address toAddress,
    uint256 amount,
    PaymentReason reason
  ) private {
    // console.log("_payJob");
    // console.log(accountNames[fromAddress]);
    // console.log(accountNames[toAddress]);
    // console.log(amount);

    bool success = tokenContract.payJob(fromAddress, toAddress, amount);
    require(success, "LilypadPayments: Pay job failed");

    emit Payment(
      dealId,
      toAddress,
      amount,
      reason,
      PaymentDirection.PaidOut
    );
  }

  function _slashEscrow(
    string memory dealId,
    address slashedAddress,
    uint256 amount,
    PaymentReason reason
  ) private {
    // console.log("_slashEscrow");
    // console.log(accountNames[slashedAddress]);
    // console.log(amount);

    bool success = tokenContract.slashEscrow(slashedAddress, amount);
    require(success, "LilypadPayments: Slash escrow failed");

    emit Payment(
      dealId,
      slashedAddress,
      amount,
      reason,
      PaymentDirection.Slashed
    );
  }
}

